mapper.insert:
MERGE INTO CART
USING DUAL
ON (ITEM_CODE = #{itemCode} AND MEM_ID = #{memId})
WHEN MATCHED THEN
	UPDATE
	SET
	ITEM_CNT = ITEM_CNT + #{itemCnt}
	WHERE MEM_ID = #{memId}
WHEN NOT MATCHED THEN
	INSERT (
		CART_NUM
		, ITEM_CODE
		, MEM_ID
		, ITEM_CNT
	) VALUES (
		(SELECT NVL(MAX(CART_NUM), 0) + 1 FROM CART)
		, #{itemCode}
		, #{memId}
		, #{itemCnt}
	)

mapper.delete:
DELETE CART
WHERE MEM_ID = #{memId} 
AND ITEM_CODE = #{itemCode}

mapper.update:
UPDATE CART
SET ITEM_CNT = #{itemCnt}
WHERE MEM_ID = #{memId}
AND ITEM_CODE = #{itemCode}

mapper.delete:
DELETE CART
WHERE MEM_ID = #{memId}
AND ITEM_CODE IN

	#{itemCode}

mapper.insert:
INSERT INTO CART(
	CART_NUM
	, MEM_ID
	, ITEM_CODE
	, ITEM_CNT
)

SELECT
 	#{cartInfo.cartNum}
 	, #{cartInfo.memId}
 	, #{cartInfo.itemCode}
 	, #{cartInfo.itemCnt}
 FROM DUAL

MERGE INTO BUY
USING DUAL
ON (ITEM_CODE = #{itemCode} AND MEM_ID = #{memId})
WHEN MATCHED THEN
	UPDATE
	SET
	ITEM_CNT = ITEM_CNT + #{itemCnt}
	WHERE MEM_ID = #{memId}
WHEN NOT MATCHED THEN
	INSERT (
		BUY_NUM
		, ITEM_CODE
		, MEM_ID
		, ITEM_CNT
	) VALUES (
		(SELECT NVL(MAX(BUY_NUM), 0) + 1 FROM BUY)
		, #{itemCode}
		, #{memId}
		, #{itemCnt}
	)

INSERT INTO BUY(
	BUY_NUM
	, MEM_ID
	, ITEM_CODE
	, ITEM_CNT
)

SELECT
 	#{buyInfo.buyNum}
 	, #{buyInfo.memId}
 	, #{buyInfo.itemCode}
 	, #{buyInfo.itemCnt}
 FROM DUAL

INSERT INTO ORDER_SHEET(
	ORDER_NUM
	, TO_NAME
	, TO_ADDR
	, TO_TELL
	, PAY_CODE
	, TOTAL_PRICE
	, DELI_MSG
	, MEM_ID
) VALUES(
	#{orderNum}
	, #{toName}
	, #{toAddr}
	, #{toTell}
	, #{payCode}
	, #{totalPrice}
	, #{deliMsg}
	, #{memId}
)

mapper.update:
UPDATE BUY
SET ORDER_NUM = #{orderNum}
WHERE ORDER_NUM IS NULL
AND MEM_ID = #{memId}

UPDATE ORDER_SHEET
SET ITEM_STATE = #{itemState}
