INSERT INTO SHOP_ITEM (
	ITEM_CODE           
	, ITEM_NAME
	, ITEM_PRICE
	, ITEM_DETAIL
	, CATE_CODE
) VALUES (
	#{itemCode}
	, #{itemName}
	, #{itemPrice}
	, #{itemDetail}
	, #{cateCode}
)

INSERT INTO ITEM_IMAGE (
	IMG_CODE
	, ORIGIN_IMG_NAME
	, ATTACHED_IMG_NAME
	, IS_MAIN
	, ITEM_CODE
)           

SELECT #{img.imgCode}
	, #{img.originImgName}
	, #{img.attachedImgName}
	, #{img.isMain}
	, #{img.itemCode}
FROM DUAL
