mapper.insert:
INSERT INTO SHOP_BUY (
	BUY_NUM
	, MEM_ID
	, ITEM_CODE
	, ITEM_CNT
	, BUY_PRICE
	, ORDER_NUM
)                    

SELECT
	#{buyInfo.buyNum}
	, #{buyInfo.memId}
	, #{buyInfo.itemCode}
	, #{buyInfo.itemCnt}
	, (SELECT ITEM_PRICE
		FROM SHOP_ITEM
		WHERE ITEM_CODE = #{buyInfo.itemCode}) * #{buyInfo.itemCnt} AS BUY_PRICE
	, #{buyInfo.orderNum}
FROM DUAL
