mapper.insert:
INSERT INTO SHOP_MEMBER (
	MEM_ID
	, MEM_PW
	, MEM_NAME
	, MEM_TELL        
	, MEM_EMAIL
	, MEM_ADDR
) VALUES (
	#{memId}
	, #{memPw}
	, #{memName}
	, #{memTell} 
	, #{memEmail}
	, #{memAddr}
)

mapper.select:
SELECT MEM_ID
	, MEM_NAME
	, IS_ADMIN
FROM SHOP_MEMBER
WHERE MEM_ID = #{memId}
AND MEM_PW = #{memPw}

SELECT MEM_EMAIL
FROM SHOP_MEMBER
WHERE MEM_ID = #{memId}
